import React from 'react'
import Dialog from '@mui/material/Dialog';
import { Box, Button, TextField, Typography } from '@mui/material';
import styled from '@emotion/styled';


const Component=styled(Box)`
height: 70vh;
width: 90vh;


`

const Image=styled(Box)`
background: coral url('https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png') center 85% no-repeat ;
height: 100%;
width:40%;
padding: 45px 35px;
`

const Wrapper= styled(Box)`
display: flex;
flex-direction: column;
padding: 45px 30px;
flex: 1;
& > div, & > button, & > p{
    margin-top :20px;
}
text-align: center;

`

const LoginButton= styled(Button)`
text-transform:none;
background:#1F74BA;
color: #ccc;
height: 48px;

`
const RequestOtp= styled(Button)`
text-transform:none;
background:#fff;
color: #287420;
height: 48px;
box-shadow: 0 2px 4px 0 rgb(0 0 0/20%)

`
const LoginDialog = ({login, setLogin}) => {
  return (<>
    <Dialog open={login} onClose={()=>setLogin(false)}>
        <Component>
            <Box style={{display: "flex", height:"100%"}}>

            <Image>
                <Typography>Login</Typography>
                <Typography>Get Access to your orders, website and </Typography>
            </Image>
            <Wrapper>
                <TextField variant='standard' label='Enter Email/Mobile Number'/>
                <TextField variant='standard' label='Enter Password/'/>
                <Typography>By Continuing, you agree to flipkart Terms of use and Privacy Policy</Typography>
                <LoginButton>Login</LoginButton>
                <Typography>Or</Typography>
                <RequestOtp>Request OTP</RequestOtp>
                <Typography>New To Flipkart? Create An Account</Typography>
            </Wrapper>
            </Box>
        </Component>

    </Dialog>
  </>

  )
}

export default LoginDialog
