import React, { useState, useSyncExternalStore } from 'react'
import Dialog from '@mui/material/Dialog';
import { Box, Button, TextField, Typography } from '@mui/material';
import styled from '@emotion/styled';


const Component=styled(Box)`
height: 70vh;
width: 90vh;


`

const Image=styled(Box)`
background: coral url('https://static-assets-web.flixcart.com/www/linchpin/fk-cp-zion/img/login_img_c4a81e.png') center 85% no-repeat ;
height: 100%;
width:40%;
padding: 45px 35px;
& > p , &> h5{
    color: #FFFFff;
    font-weight:400;

}
`

const Wrapper= styled(Box)`
display: flex;
flex-direction: column;
padding: 45px 30px;
flex: 1;
& > div, & > button, & > p{
    margin-top :20px;
}
text-align: center;

`

const LoginButton= styled(Button)`
text-transform:none;
background:#1F74BA;
color: Black;
height: 48px;
`
const RequestOtp= styled(Button)`
text-transform:none;
background:#fff;
color: #2874f0;
height: 48px;
box-shadow: 0 2px 4px 0 rgb(0 0 0/20%)

`

const Text=styled(Typography)`

font-size:12px
color:#878787 

`

const CreateAccount= styled(Typography)`
font-size: 12px;
color:#2874f0 ;
cursor: pointer;
font-weight: 400;


`

const accountinitialValues={
    Login:{
        view:"login"
    },
    signup:{
        view:"signup"
    }
}
const LoginDialog = ({login, setLogin}) => {

    const [toggleaccount, setToggleAccount]=useState(accountinitialValues.Login)

    const toggleSignUp=()=>{
        console.log("clicked")
        setToggleAccount(accountinitialValues.signup)

    }
  return (<>
    <Dialog open={login} onClose={()=>setLogin(false)} PaperProps={{sx:{maxWidth:"unset"}}}>
        <Component>
            <Box style={{display: "flex", height:"100%"}}>

            <Image>
                <Typography>Login</Typography>
                <h4>Get Access to your orders, website and Recommendations.</h4>
            </Image>

            {toggleaccount ?   <Wrapper>
                <TextField variant='standard' label='Enter Email/Mobile Number'/>
                <TextField variant='standard' label='Enter Password/'/>
                <Text>By Continuing, you agree to flipkart Terms of use and Privacy Policy</Text>
                <LoginButton>Login</LoginButton>
                <Typography>Or</Typography>
                <RequestOtp>Request OTP</RequestOtp>
                <CreateAccount onClick={toggleSignUp}>New To Flipkart? Create An Account</CreateAccount>
            </Wrapper> :
              <Wrapper>
              <TextField variant='standard' label='Enter Email/Mobile Number'/>
              <TextField variant='standard' label='Enter Password'/>
              <TextField variant='standard' label='Enter Phonenumber'/>
              <TextField variant='standard' label='Enter firstname'/>
              <TextField variant='standard' label='Enter lastname/'/>
            
       <LoginButton>Continue</LoginButton>
          </Wrapper>
             }
          
            </Box>
        </Component>

    </Dialog>
  </>

  )
}

export default LoginDialog
